import Head from "next/head";
import Link from "next/link";
import Layout from "../../components/Layout";
import { useState } from "react";
import { listBlogWithCategoriesAndTags } from "../../actions/blog";
import { API } from "../../config";
import Category from "../../components/crud/Category";
import { getTags } from "../../actions/tag";
import renderHTML from "react-render-html";
import moment from "moment";

const Blogs = ({ blogs, categories, tags, size }) => {
  const showAllBlogs = () => {
    return blogs.map((blog, index) => {
      return (
        <article key={index}>
          <div className="lead pb-4">
            <header>
              <Link href={`/blogs/${blog.slug}`}>
                <a>
                  <h2 className="pb-3 font-weight-bold">{blog.title}</h2>
                </a>
              </Link>
            </header>
            <section>
              <p className="mark ml-1 pt-2 pb-2">
                Written by {blog.postedBy.name} | published {moment(blog.createdAt).fromNow()}
              </p>
              <section>
                <p>blog Category and Tags</p>
              </section>
              <div classNam="row">
                <div className="col-md-4">image</div>
                <div className="col-md-8">
                  <section>
                    <div className="pb-3">{renderHTML(blog.excerpt)}</div>
                    <Link href={`/blogs/${blog.slug}`}>
                      <a className="btn btn-primary pt-2">Read more</a>
                    </Link>
                  </section>
                </div>
              </div>
            </section>
          </div>
          <hr />
        </article>
      );
    });
  };
  return (
    <Layout>
      <main>
        <div className="container-fluid">
          <header>
            <div className="col-md-12 pt-3">
              <h1 className="display-4 font-weight-bold text-center">
                {" "}
                Programming blogs and tutor
              </h1>
              <section>
                <p> show categories and tags</p>
              </section>
            </div>
            <div className="container-fluid">
              <div className="row">
                <div className="col-md-12">{showAllBlogs()} </div>
              </div>
            </div>
          </header>
        </div>
      </main>
    </Layout>
  );
};

//server-side rendering method from next.js    getinitialprops - can be used only on pages Not in components
Blogs.getInitialProps = () => {
  return listBlogWithCategoriesAndTags().then(data => {
    if (data.error) {
      console.log(data.error);
    } else {
      return {
        blogs: data.blogs,
        categories: data.categories,
        tags: data.tags,
        size: data.size
      };
      // once these are returned, these can be used as props.
    }
  });
};
export default Blogs;
